    func testGenerateVerseForZeroBottles() {

        let bottleSongForZeroGreenBottles = BottleSong(numberOfBottles: 0)
        let expectedVerses = "No green bottles hanging on the wall,no green bottles hanging on the wall,\nGo to the store and buy some more, 99 green bottle hanging on the wall"
        XCTAssertEqual(expectedVerses, bottleSongForZeroGreenBottles.generateVersesOfBottlesSong())

    }

    func testGenerateVerseForOneBottle() {

        let bottleSongForOneGreenBottle = BottleSong(numberOfBottles: 1)
        let expectedVerses = "1 green bottle hanging on the wall,\n1 green bottle hanging on the wall,\nAnd if this green bottle should accidentally fall,\nThere will no green bottles hanging on the wall.\n\nNo green bottles hanging on the wall,no green bottles hanging on the wall,\nGo to the store and buy some more, 99 green bottle hanging on the wall"
        XCTAssertEqual(expectedVerses, bottleSongForOneGreenBottle.generateVersesOfBottlesSong())
    }

